import './ExpenseItems.css';

function ExpenseItem(props) {
  const day = props.expense.date.toLocaleString('de-DE', { day: '2-digit' } );
  const month = props.expense.date.toLocaleString('de-DE', { month: 'long' } );
  const year = props.expense.date.toLocaleString('de-DE', { year: '4-digit' } );
  return (
    <div className="expense-item">
      <div>{day}</div>
      <div>{month}</div>
      <div>{year}</div>
      <div className="expense-item__description">
        <h2>{props.expense.title}</h2>
      </div>
      <div className="expense-item__price">${props.expense.amount}</div>
    </div>
  );
}

export default ExpenseItem;
